import React from 'react';
import { Container, Typography, Button, Box, Avatar, IconButton, AppBar, Toolbar } from '@mui/material';
import { GitHub, LinkedIn, Twitter } from '@mui/icons-material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#ff4081',
    },
  },
});

function App() {
  const links = [
    { name: 'GitHub', url: 'https://github.com' },
    { name: 'LinkedIn', url: 'https://linkedin.com' },
    { name: 'Twitter', url: 'https://twitter.com' },
    // Add more links as needed
  ];

  return (
    <ThemeProvider theme={theme}>
      <div className="background">
        {/* Navbar */}
        <AppBar position="static">
          <Toolbar>
            {/* Logo on the top left corner */}
            <Avatar
              alt="Logo"
              src="./iiith_logo.png"
              sx={{ marginRight: 2, width: 50, height: '' }}
            />
            {/* <Typography variant="h3" component="div" sx={{ flexGrow: 1 }}>
              Your Website
            </Typography> */}
            {/* Add other items in the navbar if needed */}
          </Toolbar>
        </AppBar>

        {/* Main Content */}
        <Container
          maxWidth="sm"
          sx={{
            backgroundColor: '#f5f5f5',
            borderRadius: '10px',
            padding: '30px',
            boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
            textAlign: 'center',
            marginTop: '50px',
          }}
        >
          <Avatar
            alt="Your Name"
            src="./iiit_logo.png"
            sx={{ width: 100, height: 100, margin: '0 auto' }}
          />
          <Typography variant="h4" gutterBottom>
            Your Name
          </Typography>
          <Typography variant="subtitle1" gutterBottom>
            Short bio or description
          </Typography>

          {links.map((link, index) => (
            <Box key={index} sx={{ margin: '20px 0' }}>
              <Button
                variant="contained"
                color="primary"
                href={link.url}
                target="_blank"
                fullWidth
                sx={{ padding: '15px 0' }}
              >
                {link.name}
              </Button>
            </Box>
          ))}

          <Box sx={{ marginTop: '20px' }}>
            <IconButton href="https://github.com" target="_blank">
              <GitHub />
            </IconButton>
            <IconButton href="https://linkedin.com" target="_blank">
              <LinkedIn />
            </IconButton>
            <IconButton href="https://twitter.com" target="_blank">
              <Twitter />
            </IconButton>
          </Box>
        </Container>

        {/* Footer */}
        <Box
          sx={{
            backgroundColor: '#1976d2',
            color: 'white',
            padding: '10px 0',
            textAlign: 'center',
            marginTop: '50px',
            position: 'fixed',
            bottom: 0,
            width: '100%',
          }}
        >
          <Typography variant="body1">
            © 2024 Your Website. All rights reserved.
          </Typography>
        </Box>
      </div>
    </ThemeProvider>
  );
}

export default App;
