import React from 'react';
import { Container, Grid, Card, CardContent, Typography, Button, Box, Avatar, AppBar, Toolbar } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#420303', // Blue color for navbar and footer
    },
    secondary: {
      main: '#420303',
    },
  },
});

function App() {
  const linkCategories = [
    {
      title: 'Academics',
      links: [
        { name: 'Courses', url: 'https://courses.iiit.ac.in' },
        { name: 'Old Courses Portal', url: 'https://courses.iiit.ac.in' },
        { name: 'Intranet', url: 'https://intranet.iiit.ac.in' },
        { name: 'Library', url: 'https://library.iiit.ac.in' },

      ],
    },
    {
      title: 'Research',
      links: [
        { name: 'Research Centres', url: 'https://web2py.iiit.ac.in/research_centres' },
        { name: 'Publications Portal', url: 'https://web2py.iiit.ac.in/research_centres/publications/' },
        { name: 'Seminar', url: 'https://web2py.iiit.ac.in/seminar/default/home' },

      ],
    },
    {
      title: 'Campus Life',
      links: [
        { name: 'Alumni', url: 'https://alumni.iiit.ac.in/' },
        { name: 'Blog', url: 'https://blogs.iiit.ac.in/' },
        { name: 'Courier', url: 'https://courier.iiit.ac.in/portal/main1.php' },
        { name: 'Mess', url: 'https://mess.iiit.ac.in/' },

      ],
    },
  ];
  const popularPortals = [
    { name: 'IMS', url: 'https://ims.iiit.ac.in' },
    { name: 'Intranet', url: 'https://intranet.iiit.ac.in' },
    { name: 'Mess', url: 'https://mess.iiit.ac.in' },
    { name: 'IT Self Help', url: 'https://self-help.iiit.ac.in/' },
    { name: 'Blog', url: 'https://blogs.iiit.ac.in' },
  ];

  const Miscellaneous = [
    { name: 'IT Self Help', url: 'https://self-help.iiit.ac.in/' },
    { name: 'Password Reset ( LDAP/802.1x )', url: 'https://passwordreset.iiit.ac.in/' },
  ];

  return (
    <ThemeProvider theme={theme}>
      <div className="background" style={{ backgroundColor: '#000000', minHeight: '100vh' }}>
        {/* Navbar */}
        <AppBar position="static" sx={{ backgroundColor: '#420303', height:'10vh' }}>
          <Toolbar>
            <Avatar
              alt="Logo"
              src="/path/to/your-logo.jpg"
              sx={{ marginRight: 2, width: 50, height: 50 }}
            />
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              Your Website
            </Typography>
          </Toolbar>
        </AppBar>

        {/* Main Content */}
        {/* <Container
          // maxWidth="lg"
          sx={{
            backgroundColor: 'rgba(255, 255, 255, 0.01)', // Slightly transparent background
            borderRadius: '10px',
            padding: '30px',
            marginTop: '50px',
            maxWidth: '100vw'
          }}
        > */}
          {/* Popular Portals Section */}
          <Box sx={{ marginBottom: '30px',marginLeft:'15vw',marginTop:'2vw'}}>
            <Typography variant="h5" gutterBottom sx={{ textAlign: 'center', marginBottom: '20px' , marginRight:'15vw',color:'white'}}>
              Popular Portals
            </Typography>
            <Grid container spacing={2}>
              {popularPortals.map((portal, index) => (
                <Grid item xs={12} sm={2} key={index}>
                  <Card
                    sx={{
                      backgroundColor: 'rgba(255, 255, 255, 0.3)', // Transparent card
                      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
                      borderRadius: '10px',
                      border: '1px solid rgba(255, 255, 255, 0.5)',
                      textAlign: 'center',
                      padding: '20px',
                      position: 'center',

                    }}
                  >
                    <CardContent>
                      <Button
                        variant="contained"
                        color="primary"
                        href={portal.url}
                        target="_blank"
                        fullWidth
                        sx={{
                          padding: '10px',
                          color: 'white',
                          backgroundColor: '#BO3060',
                          '&:hover': {
                            backgroundColor: '#D03D56', // Darker shade of blue for hover
                          },
                        }}
                      >
                        {portal.name}
                      </Button>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>

          <Grid container spacing={3} sx={{ borderSpacing: '20px' }}>
            {linkCategories.map((category, index) => (
              <Grid item xs={12} sm={4} key={index} sx={{ padding: '0 10px' }}>
                <Card
                  sx={{
                    backgroundColor: 'rgba(255, 255, 255, 0.2)', // Transparent card
                    boxShadow: 'none',
                    borderRadius: '10px',
                    border: '1px solid rgba(255, 255, 255, 0.3)',
                    textAlign: 'center',
                    padding: '20px', // Padding inside the card
                    height:'35vh',
                  }}
                >
                  <CardContent>
                    <Typography variant="h6" gutterBottom sx={{color:'white'}}>
                      {category.title}
                    </Typography>
                    {category.links.map((link, i) => (
                      <Box key={i} sx={{ margin: '10px 0' }}>
                        <Button
                          variant="contained"
                          color="primary"
                          href={link.url}
                          target="_blank"
                          fullWidth
                          sx={{
                            padding: '10px',
                            color: 'white',
                            backgroundColor: '#D03D56',
                            '&:hover': {
                              backgroundColor: '#420303', // Darker shade of blue for hover
                            },
                          }}
                        >
                          {link.name}
                        </Button>
                      </Box>
                    ))}
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>

        {/* </Container> */}

        <Box sx={{ marginBottom: '30px',marginLeft:'35vw',marginTop:'2vw',width:'100vw'}}>
            <Typography variant="h5" gutterBottom sx={{ textAlign: 'center', marginBottom: '20px' , marginRight:'vw',color:'white'}}>
            Miscellaneous
            </Typography>
            <Grid container spacing={2}>
              {Miscellaneous.map((portal, index) => (
                <Grid item xs={12} sm={2} key={index}>
                  <Card
                    sx={{
                      backgroundColor: 'rgba(255, 255, 255, 0.3)', // Transparent card
                      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
                      borderRadius: '10px',
                      border: '1px solid rgba(255, 255, 255, 0.5)',
                      textAlign: 'center',
                      padding: '20px',
                      position: 'center',
                      height: '10vh',
                      width: '25vh',

                    }}
                  >
                    <CardContent>
                      <Button
                        variant="contained"
                        color="primary"
                        href={portal.url}
                        target="_blank"
                        fullWidth
                        sx={{
                          padding: '10px',
                          color: 'white',
                          backgroundColor: '#BO3060',
                          '&:hover': {
                            backgroundColor: '#D03D56', // Darker shade of blue for hover
                          },
                        }}
                      >
                        {portal.name}
                      </Button>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>

        {/* Footer */}
        <Box
          sx={{
            backgroundColor: '#420303',
            color: 'white',
            padding: '10px 0',
            textAlign: 'center',
            marginTop: '50px',
            position: 'relative',
            bottom: 0,
            width: '100%',
            height:'10vh',
          }}
        >
          <Typography variant="body1">
            Â© 2024 Your Website. All rights reserved.
          </Typography>
        </Box>
      </div>
    </ThemeProvider>
  );
}

export default App;
