import React from 'react';
import { Container, Typography, Button, Box, Avatar, IconButton } from '@mui/material';
import { GitHub, LinkedIn, Twitter } from '@mui/icons-material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#ff4081',
    },
  },
});

function App() {
  const links = [
    { name: 'GitHub', url: 'https://github.com' },
    { name: 'LinkedIn', url: 'https://linkedin.com' },
    { name: 'Twitter', url: 'https://twitter.com' },
    // Add more links as needed
  ];

  return (
    <ThemeProvider theme={theme}>
      <Container
        maxWidth="sm"
        sx={{
          backgroundColor: '#f5f5f5',
          borderRadius: '10px',
          padding: '30px',
          boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
          textAlign: 'center',
          marginTop: '50px',
          // Add background image
          backgroundImage: `url(./public/iiit_hyd)`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
          minHeight: '100vh', // Ensures the container takes full height
        }}
      >
        <Avatar
          alt="Your Name"
          src="/path/to/your-avatar.jpg"
          sx={{ width: 100, height: 100, margin: '0 auto' }}
        />
        <Typography variant="h4" gutterBottom>
          Your Name
        </Typography>
        <Typography variant="subtitle1" gutterBottom>
          Short bio or description
        </Typography>

        {links.map((link, index) => (
          <Box key={index} sx={{ margin: '20px 0' }}>
            <Button
              variant="contained"
              color="primary"
              href={link.url}
              target="_blank"
              fullWidth
              sx={{ padding: '15px 0' }}
            >
              {link.name}
            </Button>
          </Box>
        ))}

        <Box sx={{ marginTop: '20px' }}>
          <IconButton href="https://github.com" target="_blank">
            <GitHub />
          </IconButton>
          <IconButton href="https://linkedin.com" target="_blank">
            <LinkedIn />
          </IconButton>
          <IconButton href="https://twitter.com" target="_blank">
            <Twitter />
          </IconButton>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
