import React from 'react';
import { Container, Grid, Card, CardContent, Typography, Button, Box, Avatar, AppBar, Toolbar } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#ff4081',
    },
  },
});

function App() {
  const linkCategories = [
    {
      title: 'Social Links',
      links: [
        { name: 'GitHub', url: 'https://github.com' },
        { name: 'LinkedIn', url: 'https://linkedin.com' },
        { name: 'Twitter', url: 'https://twitter.com' },
      ],
    },
    {
      title: 'Educational Links',
      links: [
        { name: 'Coursera', url: 'https://coursera.org' },
        { name: 'edX', url: 'https://edx.org' },
        { name: 'Udemy', url: 'https://udemy.com' },
      ],
    },
    {
      title: 'Entertainment Links',
      links: [
        { name: 'Netflix', url: 'https://netflix.com' },
        { name: 'YouTube', url: 'https://youtube.com' },
        { name: 'Spotify', url: 'https://spotify.com' },
      ],
    },
  ];

  return (
    <ThemeProvider theme={theme}>
      <div className="background">
        {/* Navbar */}
        <AppBar position="static">
          <Toolbar>
            <Avatar
              alt="Logo"
              src="/path/to/your-logo.jpg"
              sx={{ marginRight: 2, width: 50, height: 50 }}
            />
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              Your Website
            </Typography>
          </Toolbar>
        </AppBar>

        {/* Main Content */}
        <Container
          maxWidth="lg"
          sx={{
            backgroundColor: 'rgba(255, 255, 255, 0.8)', // Slightly transparent background
            borderRadius: '10px',
            padding: '30px',
            marginTop: '50px',
          }}
        >
          <Grid container spacing={3}>
            {linkCategories.map((category, index) => (
              <Grid item xs={12} sm={4} key={index}>
                <Card
                  sx={{
                    backgroundColor: 'rgba(255, 255, 255, 0.1)', // Transparent card
                    boxShadow: 'none',
                    borderRadius: '10px',
                    border: '1px solid rgba(255, 255, 255, 0.2)',
                    textAlign: 'center',
                  }}
                >
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      {category.title}
                    </Typography>
                    {category.links.map((link, i) => (
                      <Box key={i} sx={{ margin: '10px 0' }}>
                        <Button
                          variant="outlined"
                          color="primary"
                          href={link.url}
                          target="_blank"
                          fullWidth
                          sx={{
                            padding: '10px',
                            color: 'white',
                            borderColor: 'white',
                            '&:hover': {
                              backgroundColor: 'rgba(255, 255, 255, 0.2)',
                            },
                          }}
                        >
                          {link.name}
                        </Button>
                      </Box>
                    ))}
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>

        {/* Footer */}
        <Box
          sx={{
            backgroundColor: '#1976d2',
            color: 'white',
            padding: '10px 0',
            textAlign: 'center',
            marginTop: '50px',
            position: 'fixed',
            bottom: 0,
            width: '100%',
          }}
        >
          <Typography variant="body1">
            Â© 2024 Your Website. All rights reserved.
          </Typography>
        </Box>
      </div>
    </ThemeProvider>
  );
}

export default App;
